body {
  font-family: "Josefin Sans", sans-serif;
}

.heading__primary {
  color: #fff;
  font-size: 6rem;
  font-weight: 500;
  letter-spacing: 5px;
  line-height: 1.2;
}
.heading__secondary {
  font-size: 5rem;
  font-weight: 400;
  letter-spacing: 3px;
  color: #333945;
  line-height: 1.2;
  text-transform: capitalize;
}
.heading__secondary span {
  display: block;
}

.btn,
.btn:link,
.btn:visited {
  display: inline-block;
  background: none;
  border: none;
  text-decoration: none;
  color: #fff;
  font-size: 2.2rem;
  font-weight: 400;
  background-color: #333945;
  padding: 2rem 5rem;
  line-height: inherit;
  border-radius: 3px;
  cursor: pointer;
  transition: all 0.3s ease-in-out;
}
.btn:hover,
.btn:link:hover,
.btn:visited:hover {
  background-color: #282d36;
}
.btn.btn__primary,
.btn:link.btn__primary,
.btn:visited.btn__primary {
  background-color: #1a237e;
}
.btn.btn__primary:hover,
.btn:link.btn__primary:hover,
.btn:visited.btn__primary:hover {
  background-color: #161d69;
}

.card {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  position: relative;
  height: 54rem;
  background-color: #fff;
  box-shadow: 5px 5px 4rem rgba(0, 0, 0, 0.12);
  border-radius: 0.3rem;
  color: #fff;
  z-index: 1;
}
.card::before {
  content: "";
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 60%;
  background-image: linear-gradient(to right, #4a148c, #2196f3);
  clip-path: polygon(0 0, 100% 0, 100% 80%, 0 100%);
  border-top-left-radius: 0.3rem;
  border-top-right-radius: 0.3rem;
  z-index: -1;
}
.card__title {
  font-size: 2rem;
  font-weight: 500;
  text-transform: uppercase;
}
.card__text {
  font-size: 1.8rem;
  font-weight: 300;
  text-transform: capitalize;
  margin: 0.5rem 0 1rem 0;
}
.card__price {
  font-size: 3rem;
  font-weight: 700;
}
.card__img {
  display: block;
  width: 50%;
  margin: 2rem 0 3rem 0;
  transform: rotate(10deg);
}
.card__btn, .card__btn:link, .card__btn:visited {
  display: inline-block;
  background: none;
  border: none;
  text-decoration: none;
  color: #fff;
  font-size: 2.2rem;
  font-weight: 400;
  background-color: #1a237e;
  padding: 1.3rem 3.5rem;
  line-height: inherit;
  border-radius: 3px;
  cursor: pointer;
  transition: all 0.3s ease-in-out;
