.product__price {
  font-size: 3.6rem;
  font-weight: 500;
  color: #fff;
  margin-top: 4rem;
}
.product__price > span {
  font-size: 6rem;
  font-weight: 700;
}
.product__img {
  display: block;
  width: 85%;
  margin-left: auto;
}

.slick-dots {
  bottom: -5rem;
}

.testimonial {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: #333945;
  font-size: 1.6rem;
  padding: 0 2rem;
  cursor: grab;
}
.testimonial__text {
