.btn:link,
.btn:visited {
  display: inline-block;
  background: none;
  border: none;
  text-decoration: none;
  color: #fff;
  font-size: 2.2rem;
  font-weight: 400;
  background-color: #333945;
  padding: 2rem 5rem;
  line-height: inherit;
  border-radius: 3px;
  cursor: pointer;
  transition: all 0.3s ease-in-out;
